[
    // ðŸš€ KOR'TANA AI DEVELOPMENT SHORTCUTS

    // GitHub Copilot Power Commands
    {
        "key": "ctrl+shift+k",
        "command": "github.copilot.chat.explainThis",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+alt+k",
        "command": "github.copilot.chat.fixThis", 
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+shift+alt+k",
        "command": "github.copilot.generate",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+k ctrl+i",
        "command": "github.copilot.chat.review",
        "when": "editorTextFocus"
    },

    // AI Chat & Assistance
    {
        "key": "ctrl+shift+a",
        "command": "github.copilot.chat.open"
    },
    {
        "key": "ctrl+alt+a",
        "command": "github.copilot.chat.newChat"
    },
    {
        "key": "ctrl+shift+enter",
        "command": "github.copilot.chat.acceptAnswer",
        "when": "github.copilot.chat.focused"
    },

    // Code Navigation & Editing Power
    {
        "key": "ctrl+shift+d",
        "command": "editor.action.duplicateSelection",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+shift+l",
        "command": "editor.action.selectHighlights",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+alt+up",
        "command": "editor.action.insertCursorAbove",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+alt+down",
        "command": "editor.action.insertCursorBelow",
        "when": "editorTextFocus"
    },

    // Quick File Operations for Kor'tana Project
    {
        "key": "ctrl+shift+n",
        "command": "workbench.action.files.newUntitledFile"
    },
    {
        "key": "ctrl+k ctrl+o",
        "command": "workbench.action.files.openFolder"
    },
    {
        "key": "ctrl+shift+s",
        "command": "workbench.action.files.saveAll"
    },

    // Terminal & Console Power
    {
        "key": "ctrl+shift+`",
        "command": "workbench.action.terminal.new"
    },
    {
        "key": "ctrl+alt+`",
        "command": "workbench.action.terminal.split"
    },
    {
        "key": "ctrl+shift+c",
        "command": "workbench.action.terminal.copySelection",
        "when": "terminalTextSelected"
    },

    // Python & Testing Shortcuts
    {
        "key": "ctrl+shift+t",
        "command": "python.runtests",
        "when": "editorLangId == python"
    },
    {
        "key": "ctrl+f5",
        "command": "python.execInTerminal",
        "when": "editorLangId == python"
    },
    {
        "key": "ctrl+shift+p",
        "command": "python.runLinting",
        "when": "editorLangId == python"
    },

    // Git & Version Control
    {
        "key": "ctrl+shift+g",
        "command": "workbench.view.scm"
    },
    {
        "key": "ctrl+k ctrl+c",
        "command": "git.commitAll"
    },
    {
        "key": "ctrl+k ctrl+p",
        "command": "git.push"
    },

    // Search & Replace Power
    {
        "key": "ctrl+shift+f",
        "command": "workbench.action.findInFiles"
    },
    {
        "key": "ctrl+shift+h",
        "command": "workbench.action.replaceInFiles"
    },
    {
        "key": "ctrl+alt+f",
        "command": "editor.action.formatDocument",
        "when": "editorTextFocus"
    },

    // Debug & Problem Solving
    {
        "key": "f9",
        "command": "editor.debug.action.toggleBreakpoint",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+f9",
        "command": "editor.debug.action.toggleInlineBreakpoint",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+shift+f9",
        "command": "workbench.debug.viewlet.action.removeAllBreakpoints"
    },

    // Workspace & Panel Management
    {
        "key": "ctrl+k ctrl+w",
        "command": "workbench.action.closeAllEditors"
    },
    {
        "key": "ctrl+k ctrl+u",
        "command": "workbench.action.reopenClosedEditor"
    },
    {
        "key": "ctrl+alt+e",
        "command": "workbench.view.explorer"
    },

    // AI Code Actions & Quick Fixes
    {
        "key": "ctrl+.",
        "command": "editor.action.quickFix",
        "when": "editorTextFocus"
    },
    {
        "key": "ctrl+shift+.",
        "command": "editor.action.refactor",
        "when": "editorTextFocus"
    },
    {
        "key": "alt+enter",
        "command": "editor.action.sourceAction",
        "when": "editorTextFocus"
    }
]

